import React, { useState } from "react";

const AddAttendeeForm = (props) => {
  const initialFormState = {
    name: "",
    username: "",
    sex: "",
    yearOfBirth: 1,
    university: "",
    major: "",
  };
  const [attendee, setAttendee] = useState(initialFormState);

  const handleInputChange = (event) => {
    const { name, value } = event.target;

    setAttendee({ ...attendee, [name]: value });
  };

  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        if (!attendee.name || !attendee.username) return;

        props.addAttendee(attendee);
        attendee(initialFormState);
      }}
    >
      <label>Name</label>
      <input
        type="text"
        name="name"
        required="yes"
        value={attendee.name}
        onChange={handleInputChange}
      />

      <label>Username</label>
      <input
        type="text"
        name="username"
        required="yes"
        value={attendee.username}
        onChange={handleInputChange}
      />

      <label>Sex</label>
      <input
        type="text"
        name="sex"
        required="yes"
        value={attendee.sex}
        onChange={handleInputChange}
      />

      <label>Year Of Birth</label>
      <input
        type="text"
        name="yearOfBirth"
        required="yes"
        value={attendee.yearOfBirth}
        onChange={handleInputChange}
      />

      <label>University</label>
      <input
        type="text"
        name="university"
        required="yes"
        value={attendee.university}
        onChange={handleInputChange}
      />

      <label>Major</label>
      <input
        type="text"
        name="major"
        required="yes"
        value={attendee.major}
        onChange={handleInputChange}
      />

      <button>Add new user</button>
    </form>
  );
};

export default AddAttendeeForm;
